import React from "react";
import store from "../store";
import "./Clients.css";

const clientData = store.getState().clientReducer;

/**
 * sort clients, last_name, first_name
 */
function clientName() {
  clientData.sort((a, b) => {
    if (a.last_name < b.last_name) {
      return -1;
    }
    if (a.last_name > b.last_name) {
      return 1;
    }
    if (a.first_name < b.first_name) {
      return -1;
    }
    if (a.first_name > b.first_name) {
      return 1;
    }
    return 0;
  });
  clientItems();
}

function clientYear() {
  console.log(clientData);
  clientData.sort((a, b) => {
    if (a.birthyear < b.birthyear) {
      return -1;
    }
    if (a.birthyear > b.birthyear) {
      return 1;
    }
    return 0;
  });
  clientItems();
}

function clientItems() {
  clientData.map((item) => (
    <li key={item.id}>
      {item.first_name} {item.last_name}, {item.birthyear}
      {"........."}
      <button className="delete-btn">
        <i className="gg-trash trash-icon"></i>
      </button>
      {"........."}
    </li>
  ));
}

export default function Clients() {
  return (
    <div>
      <button onClick={clientName}>sort by name</button>
      <br />
      <button onClick={clientYear}>sort by birthyear</button>
      <br />
      <br />
      <hr /> {clientItems}
    </div>
  );
}
